mapper.select:
select email, code, is_verified
from authentication_tbl
where code = #{code}

mapper.insert:
insert into member_TBL (
member_ID, member_PWD, member_NAME, member_EMAIL, provider, provider_id
)
values (
#{memberId}, #{memberPwd}, #{memberName}, #{memberEmail}, #{provider}, #{providerId}
)

mapper.select:
select member_ID, member_PWD, member_NAME, member_EMAIL, JOIN_DATE, member_IMG, MEMBER_ROLE
from member_TBL
where member_ID = #{memberId}

mapper.update:
update member_TBL
set member_pwd   = #{memberPwd},
    member_email = #{memberEmail},
    member_img   = #{memberImg}
where member_ID = #{memberId}
