mapper.update:
update MEMBER_TBL
set MEMBER_PWD   = #{memberPwd},
    MEMBER_EMAIL = #{memberEmail},
    MEMBER_IMG   = #{memberImg}
where MEMBER_ID = #{memberId}
  and PROVIDER = 'ZIP'

mapper.select:
select count(*)
from MEMBER_TBL
where MEMBER_ID = #{memberId}

select count(*)
from MEMBER_TBL
where MEMBER_EMAIL = #{memberEmail}

