mapper.insert:
insert into USER_TBL (
USER_ID, USER_PWD, USER_NAME, USER_EMAIL, USER_PHONE
)
values (
#{userId}, #{userPwd}, #{userName}, #{userEmail}, #{userPhone}
)

mapper.select:
select USER_ID, USER_PWD, USER_NAME, USER_EMAIL, USER_PHONE, JOIN_DATE, USER_IMG
from USER_TBL
where USER_ID = #{userId}

mapper.update:
update USER_TBL
set user_pwd   = #{userPwd},
    user_email = #{userEmail},
    user_phone = #{userPhone},
    user_img   = #{userImg}
where USER_ID = #{userId}
